import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import UserContext from '../UserContext';  // Ensure the correct path based on your project

function Navbar() {
  const { user, logout } = useContext(UserContext);  // Accessing user and logout function from context

  const handleLogout = (event) => {
    event.preventDefault();  // Prevent default anchor behavior
    logout();                // Call logout function from context
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <Link className="navbar-brand" to="/">Home</Link>
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav ml-auto">
          <li className="nav-item">
            <Link className="nav-link" to="/notes">Notes</Link>
          </li>
          {user ? (
            <>
              <li className="nav-item">
                {/* Using <button> or <Link> with onClick for logout */}
                <button onClick={handleLogout} className="nav-link btn btn-link" style={{padding: 0}}>Logout</button>
              </li>
            </>
          ) : (
            <>
              <li className="nav-item">
                <Link className="nav-link" to="/signup">Register</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/login">Login</Link>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
